import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import AdminHomePage from "../../AdminHomePage";
import useMascotaViewModel from "../../../viewmodels/useMascotaViewModel";

const EditarMascota = () => {
    const navigate = useNavigate();
    const { id } = useParams(); // ID de la mascota desde la URL

    // 游댠 CLIENTE TEMPORAL (luego puedes reemplazarlo por variable din치mica)
    const CLIENTE_ID = "000000000000000000000001";

    const { fetchMascotaById, updateMascota } = useMascotaViewModel(CLIENTE_ID);

    const [form, setForm] = useState({
        nombre: "",
        especie: "",
        raza: "",
        edad: "",
        peso: "",
    });

    // Cargar mascota al entrar a la p치gina
    useEffect(() => {
        const loadMascota = async () => {
            const data = await fetchMascotaById(id);
            setForm({
                nombre: data.nombre,
                especie: data.especie,
                raza: data.raza,
                edad: data.edad,
                peso: data.peso,
            });
        };
        loadMascota();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateMascota(id, form);
        navigate("/admin/clientes/mascotas");
    };

    return (
        <AdminHomePage>
            <div className="container mt-4">
                <h2>Editar Mascota</h2>

                <form onSubmit={handleSubmit} className="mt-3">
                    <input
                        className="form-control mb-3"
                        value={form.nombre}
                        placeholder="Nombre"
                        onChange={(e) => setForm({ ...form, nombre: e.target.value })}
                    />

                    <input
                        className="form-control mb-3"
                        value={form.especie}
                        placeholder="Especie"
                        onChange={(e) => setForm({ ...form, especie: e.target.value })}
                    />

                    <input
                        className="form-control mb-3"
                        value={form.raza}
                        placeholder="Raza"
                        onChange={(e) => setForm({ ...form, raza: e.target.value })}
                    />

                    <input
                        type="number"
                        className="form-control mb-3"
                        value={form.edad}
                        placeholder="Edad"
                        onChange={(e) => setForm({ ...form, edad: e.target.value })}
                    />

                    <input
                        type="number"
                        className="form-control mb-3"
                        value={form.peso}
                        placeholder="Peso"
                        onChange={(e) => setForm({ ...form, peso: e.target.value })}
                    />

                    <button className="btn btn-success">Guardar Cambios</button>
                </form>
            </div>
        </AdminHomePage>
    );
};

export default EditarMascota;
