import { useState } from "react";

const API_URL = "http://98.91.150.2:5000/api/clientes";  
// ahora apuntas a /clientes como debe ser

const useMascotaViewModel = (clienteId) => {
    const [mascotas, setMascotas] = useState([]);
    const [mascota, setMascota] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // ================================
    // Obtener todas las mascotas
    // ================================
    const fetchMascotas = async () => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${clienteId}/mascotas`);
            const data = await res.json();

            // asegurar que mascotas SIEMPRE sea array
            setMascotas(Array.isArray(data) ? data : []);
        } catch (err) {
            setError(err.message);
            setMascotas([]); // Evita que React explote
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Obtener mascota por ID
    // ================================
    const fetchMascotaById = async (idMascota) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${clienteId}/mascotas/${idMascota}`);
            const data = await res.json();
            setMascota(data || null);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Crear mascota
    // ================================
    const createMascota = async (nuevaMascota) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${clienteId}/mascotas`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(nuevaMascota)
            });
            return await res.json();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Editar mascota
    // ================================
    const updateMascota = async (idMascota, mascotaEditada) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${clienteId}/mascotas/${idMascota}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(mascotaEditada)
            });
            return await res.json();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Eliminar mascota
    // ================================
    const deleteMascota = async (idMascota) => {
        try {
            setLoading(true);
            await fetch(`${API_URL}/${clienteId}/mascotas/${idMascota}`, {
                method: "DELETE"
            });
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return {
        mascotas,
        mascota,
        loading,
        error,
        fetchMascotas,
        fetchMascotaById,
        createMascota,
        updateMascota,
        deleteMascota
    };
};

export default useMascotaViewModel;
