import React, { useEffect } from "react";
import AdminHomePage from "../../AdminHomePage";

// ===============================
// ðŸ”¥ IMPORTAR EL VIEWMODEL CORRECTO
// ===============================
import useMascotaViewModel from "../../../viewmodels/useMascotaViewModel";

import { useNavigate } from "react-router-dom";

const ListadoMascotas = () => {
    const navigate = useNavigate();

    // CLIENTE TEMPORAL (reemplaza luego por cliente real seleccionado)
    const CLIENTE_ID = "000000000000000000000001";

    // ==========================================
    // ðŸ”¥ IMPORTANTE: INCLUIR fetchMascotas
    // ==========================================
    const { 
        mascotas, 
        loading, 
        fetchMascotas,     // <--- LO AGREGAMOS
        deleteMascota 
    } = useMascotaViewModel(CLIENTE_ID);

    // ==========================================
    // ðŸ”¥ NUEVO: LLAMAR A fetchMascotas CUANDO CARGA LA PÃGINA
    // ==========================================
    useEffect(() => {
    fetchMascotas();   // Esto carga las mascotas al entrar
}, []); // Solo una vez al entrar

    if (loading) return <p>Cargando mascotas...</p>;

    return (
        <AdminHomePage>
            <div className="container mt-4">
                <h2 className="mb-3">Listado de Mascotas</h2>

                <button
                    className="btn btn-primary mb-3"
                    onClick={() => navigate("/admin/clientes/mascotas/nueva")}
                >
                    Registrar Nueva Mascota
                </button>

                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Especie</th>
                            <th>Raza</th>
                            <th>Edad</th>
                            <th>Peso</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>

                        {/* ðŸ”¥ Mostrar todas las mascotas */}
                        {mascotas.map((m) => (
                            <tr key={m._id}>
                                <td>{m.nombre}</td>
                                <td>{m.especie}</td>
                                <td>{m.raza}</td>
                                <td>{m.edad}</td>
                                <td>{m.peso} kg</td>

                                <td>
                                    <button
                                        className="btn btn-warning btn-sm me-2"
                                        onClick={() =>
                                            navigate(`/admin/clientes/mascotas/editar/${m._id}`)
                                        }
                                    >
                                        Editar
                                    </button>

                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => deleteMascota(m._id)}
                                    >
                                        Eliminar
                                    </button>
                                </td>
                            </tr>
                        ))}

                    </tbody>
                </table>
            </div>
        </AdminHomePage>
    );
};

export default ListadoMascotas;
