import { useState } from "react";

const API_BASE = "http://98.91.150.2:5000/api/clientes";

// Cámbialo a tu IP pública si usas AWS ej:
// const API_BASE = "http://TU_IP_PUBLICA:5000/api/clientes";

const useMascotaViewModel = (idCliente) => {
    const [mascotas, setMascotas] = useState([]);
    const [mascota, setMascota] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // ================================
    // Obtener listado
    // ================================
    const fetchMascotas = async () => {
        try {
            setLoading(true);
            const res = await fetch(`${API_BASE}/${idCliente}/mascotas`);
            const data = await res.json();
            setMascotas(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Obtener por ID
    // ================================
    const fetchMascotaById = async (idMascota) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_BASE}/${idCliente}/mascotas/${idMascota}`);
            const data = await res.json();
            setMascota(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Crear
    // ================================
    const createMascota = async (nuevaMascota) => {
        const res = await fetch(`${API_BASE}/${idCliente}/mascotas`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(nuevaMascota)
        });
        return await res.json();
    };

    // ================================
    // Editar
    // ================================
    const updateMascota = async (idMascota, mascotaEditada) => {
        const res = await fetch(`${API_BASE}/${idCliente}/mascotas/${idMascota}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(mascotaEditada)
        });
        return await res.json();
    };

    // ================================
    // Eliminar
    // ================================
    const deleteMascota = async (idMascota) => {
        await fetch(`${API_BASE}/${idCliente}/mascotas/${idMascota}`, {
            method: "DELETE"
        });
    };

    return {
        mascotas,
        mascota,
        loading,
        error,
        fetchMascotas,
        fetchMascotaById,
        createMascota,
        updateMascota,
        deleteMascota
    };
};

export default useMascotaViewModel;
