import { useState, useEffect } from 'react';
import axios from 'axios';

// ⚠️ CORRECCIÓN: Usar la misma IP/Puerto que el resto de tu app (98.91.150.2:5000 o localhost:5000)
const API_URL = 'http://98.91.150.2:5000/api/boletas'; 

const useBoletaViewModel = () => {
  const [boletas, setBoletas] = useState([]);

  // --- CARGAR BOLETAS (READ) ---
  const cargarBoletas = async () => {
      try {
          const res = await axios.get(API_URL);

          const boletasAdaptadas = res.data.map(b => ({
              id: b._id,
              fecha: new Date(b.fecha).toLocaleDateString('es-CL', { 
                  year: 'numeric', month: '2-digit', day: '2-digit', 
                  hour: '2-digit', minute: '2-digit'
              }),
              total: b.total,
              estado: b.estado,
              userId: b.usuario?._id || 'N/A',
              // Manejo seguro por si el usuario fue eliminado
              cliente: b.usuario ? `${b.usuario.nombre} ${b.usuario.apellidos}` : 'Usuario Eliminado'
          }));

          setBoletas(boletasAdaptadas);
      } catch (error) {
          console.error("Error cargando boletas", error);
      }
  };

  useEffect(() => {
    cargarBoletas();
  }, []);

  // --- CREAR BOLETA (CREATE) ---
  const addBoleta = async (compraData) => {
      try {
          // Axios lanzará una excepción si el status no es 2xx (ej: 400 por falta de stock)
          await axios.post(API_URL, compraData);
          await cargarBoletas(); 
          return true;
      } catch (error) {
          console.error("Error al crear la boleta:", error.response?.data?.message || error.message);
          // Re-lanzamos el error para que CompraPage pueda mostrar la alerta o redirigir a error
          throw error; 
      }
  };

  const getBoletasByUserId = (userId) => {
    return boletas.filter(b => b.userId === userId);
  };

  return { 
      boletas, 
      getBoletasByUserId,
      addBoleta 
  };
};

export default useBoletaViewModel;
