import React, { useState } from "react";
import AdminHomePage from "../../AdminHomePage";
import { useNavigate } from "react-router-dom";
import useMascotaViewModel from "../../../viewmodels/useMascotaViewModel";

const NuevaMascota = () => {
    const navigate = useNavigate();

    // CLIENTE TEMPORAL (después lo haces dinámico)
    const CLIENTE_ID = "000000000000000000000001";

    const { addMascota } = useMascotaViewModel(CLIENTE_ID);

    const [form, setForm] = useState({
        nombre: "",
        especie: "",
        raza: "",
        edad: "",
        peso: "",
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        await addMascota(form);
        navigate("/admin/clientes/mascotas");
    };

    return (
        <AdminHomePage>
            <div className="container mt-4">
                <h2>Registrar Mascota</h2>

                <form onSubmit={handleSubmit} className="mt-3">

                    <input
                        className="form-control mb-3"
                        placeholder="Nombre de la Mascota"
                        value={form.nombre}
                        onChange={(e) =>
                            setForm({ ...form, nombre: e.target.value })
                        }
                    />

                    <input
                        className="form-control mb-3"
                        placeholder="Especie (Perro, Gato...)"
                        value={form.especie}
                        onChange={(e) =>
                            setForm({ ...form, especie: e.target.value })
                        }
                    />

                    <input
                        className="form-control mb-3"
                        placeholder="Raza"
                        value={form.raza}
                        onChange={(e) =>
                            setForm({ ...form, raza: e.target.value })
                        }
                    />

                    <input
                        type="number"
                        className="form-control mb-3"
                        placeholder="Edad"
                        value={form.edad}
                        onChange={(e) =>
                            setForm({ ...form, edad: e.target.value })
                        }
                    />

                    <input
                        type="number"
                        className="form-control mb-3"
                        placeholder="Peso (kg)"
                        value={form.peso}
                        onChange={(e) =>
                            setForm({ ...form, peso: e.target.value })
                        }
                    />

                    <button className="btn btn-success">Guardar Mascota</button>
                </form>
            </div>
        </AdminHomePage>
    );
};

export default NuevaMascota;
