import { useState } from "react";

const API_URL = "http://localhost:5000/api/mascotas"; 
// Cámbialo por tu IP pública si usas AWS

const useMascotaViewModel = () => {
    const [mascotas, setMascotas] = useState([]);
    const [mascota, setMascota] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    // ================================
    // Obtener todas las mascotas
    // ================================
    const fetchMascotas = async () => {
        try {
            setLoading(true);
            const res = await fetch(API_URL);
            const data = await res.json();
            setMascotas(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Obtener mascota por ID
    // ================================
    const fetchMascotaById = async (id) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${id}`);
            const data = await res.json();
            setMascota(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Crear mascota
    // ================================
    const createMascota = async (nuevaMascota) => {
        try {
            setLoading(true);
            const res = await fetch(API_URL, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(nuevaMascota)
            });
            return await res.json();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Editar mascota
    // ================================
    const updateMascota = async (id, mascotaEditada) => {
        try {
            setLoading(true);
            const res = await fetch(`${API_URL}/${id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(mascotaEditada)
            });
            return await res.json();
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // ================================
    // Eliminar mascota
    // ================================
    const deleteMascota = async (id) => {
        try {
            setLoading(true);
            await fetch(`${API_URL}/${id}`, {
                method: "DELETE"
            });
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    return {
        mascotas,
        mascota,
        loading,
        error,
        fetchMascotas,
        fetchMascotaById,
        createMascota,
        updateMascota,
        deleteMascota
    };
};

export default useMascotaViewModel;
