import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import AdminTemplate from "../../templates/AdminTemplate";
import useUserViewModel from "../../viewmodels/useUserViewModel";

const ListarUsuarios = () => {
  const navigate = useNavigate();
  const { users, fetchUsers, deleteUser } = useUserViewModel();

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const handleDelete = async (id) => {
    if (!window.confirm("¿Seguro que deseas eliminar este usuario?")) return;
    await deleteUser(id);
  };

  return (
    <AdminTemplate>
      <main className="flex-grow-1" id="main-content" role="main">
        <div className="container-fluid py-4">

          <nav aria-label="breadcrumb" className="mb-3">
            <ol className="breadcrumb mb-0">
              <li className="breadcrumb-item"><a href="/Admin">Administración</a></li>
              <li className="breadcrumb-item active" aria-current="page">Usuarios</li>
            </ol>
          </nav>

          <h1 className="h4 mb-4">Listado de Usuarios</h1>

          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Apellidos</th>
                <th>Correo</th>
                <th>Rol</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>

              {users.length === 0 && (
                <tr><td colSpan="5">No hay usuarios registrados.</td></tr>
              )}

              {users.map((u) => (
                <tr key={u.id}>
                  <td>{u.nombre}</td>
                  <td>{u.apellidos}</td>
                  <td>{u.correo}</td>
                  <td>{u.rol}</td>
                  <td>
                    <button
                      className="btn btn-warning btn-sm me-2"
                      onClick={() => navigate(`/admin/usuarios/editarusuario/${u.id}`)}
                    >
                      Editar
                    </button>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => handleDelete(u.id)}
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}

            </tbody>
          </table>
        </div>
      </main>
    </AdminTemplate>
  );
};

export default ListarUsuarios;
