import React, { useEffect } from "react";
import AdminHomePage from "../../AdminHomePage";
import useMascotaViewModel from "../../../viewmodels/useMascotaViewModel";
import { useNavigate } from "react-router-dom";

const ListadoMascotas = () => {

    const navigate = useNavigate();

    // ðŸ“Œ CLIENTE TEMPORAL â€” luego lo reemplazas con el cliente real
    const CLIENTE_ID = "000000000000000000000001";

    // ðŸ“Œ Desestructuramos TODO SOLO UNA VEZ
    const {
        mascotas,
        loading,
        fetchMascotas,
        deleteMascota
    } = useMascotaViewModel(CLIENTE_ID);

    // ==========================================
    // ðŸš€ useEffect que carga las mascotas
    // ==========================================
    // eslint-disable-next-line react-hooks/exhaustive-deps
    useEffect(() => {
        fetchMascotas();
    }, []);

    if (loading) return <p>Cargando mascotas...</p>;

    return (
        <AdminHomePage>
            <div className="container mt-4">
                <h2 className="mb-3">Listado de Mascotas</h2>

                <button
                    className="btn btn-primary mb-3"
                    onClick={() => navigate("/admin/clientes/mascotas/nueva")}
                >
                    Registrar Nueva Mascota
                </button>

                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Especie</th>
                            <th>Raza</th>
                            <th>Edad</th>
                            <th>Peso</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Array.isArray(mascotas) && mascotas.length > 0 ? (
                            mascotas.map((m) => (
                                <tr key={m._id}>
                                    <td>{m.nombre}</td>
                                    <td>{m.especie}</td>
                                    <td>{m.raza}</td>
                                    <td>{m.edad}</td>
                                    <td>{m.peso} kg</td>

                                    <td>
                                        <button
                                            className="btn btn-warning btn-sm me-2"
                                            onClick={() =>
                                                navigate(
                                                    `/admin/clientes/mascotas/editar/${m._id}`
                                                )
                                            }
                                        >
                                            Editar
                                        </button>

                                        <button
                                            className="btn btn-danger btn-sm"
                                            onClick={() => deleteMascota(m._id)}
                                        >
                                            Eliminar
                                        </button>
                                    </td>
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan="6" className="text-center">
                                    No hay mascotas registradas
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </AdminHomePage>
    );
};

export default ListadoMascotas;
