import React, { useState } from "react";
import AdminTemplate from "../../../templates/AdminTemplate";
import useMascotaViewModel from "../../../viewmodels/useMascotaViewModel";
import Button from "../../../components/atoms/Button";

const ListarMascotasPage = () => {
  const [rut, setRut] = useState("");

  const {
    mascotas,
    loading,
    fetchMascotas,
    deleteMascota
  } = useMascotaViewModel(rut);

  const handleBuscar = () => {
    if (!rut) {
      alert("Debes ingresar un RUT");
      return;
    }
    fetchMascotas();
  };

  return (
    <AdminTemplate>
      <main className="flex-grow-1 p-4">

        <h2 className="mb-4">Listado de Mascotas</h2>

        {/* Input para RUT */}
        <div className="mb-3 d-flex gap-2">
          <input
            type="text"
            className="form-control"
            placeholder="Ingrese RUT del cliente"
            value={rut}
            onChange={(e) => setRut(e.target.value)}
          />
          <Button label="Buscar" onClick={handleBuscar} />
        </div>

        {/* Tabla */}
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Especie</th>
                <th>Raza</th>
                <th>Edad</th>
                <th>Peso</th>
                <th>Acciones</th>
              </tr>
            </thead>

            <tbody>
              {loading && (
                <tr>
                  <td colSpan="6">Cargando...</td>
                </tr>
              )}

              {!loading && mascotas.length === 0 && (
                <tr>
                  <td colSpan="6">No hay mascotas para este cliente.</td>
                </tr>
              )}

              {mascotas.map((m) => (
                <tr key={m._id}>
                  <td>{m.nombre}</td>
                  <td>{m.especie}</td>
                  <td>{m.raza}</td>
                  <td>{m.edad}</td>
                  <td>{m.peso}</td>
                  <td className="d-flex gap-2">
                    <Button
                      label="Editar"
                      href={`/admin/clientes/mascotas/editar/${m._id}`}
                    />
                    <Button
                      label="Eliminar"
                      variant="danger"
                      onClick={() => deleteMascota(m._id)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </main>
    </AdminTemplate>
  );
};

export default ListarMascotasPage;
